$('#pa_name').on('change', function(){
    // console.log("Saving value " + $(this).val());
    $(this).data('val', $(this).val());
    var prev = $(this).data('val');
    var current = $(this).val();
    var gencode = $('#codegen').val();
    var paname = $('#pa_name').val();
    var id_main = $('#id_main').val();
    swal({
      title: "Are you sure you want to change the data?",
      text: "Once update, you will not be able to revert it!",
      icon: "warning",
      buttons: true,
      dangerMode: true,
      showDenyButton: true,
      showCancelButton: true,
      confirmButtonColor: '#009999',
      cancelButtonColor: 'Crimson',
    },
    function(isConfirm){

   if (isConfirm){
     // swal("Shortlisted!", "Candidates are successfully shortlisted!", "success");
     $.ajax({
      url : BASE_URL+'pasu/pamain/update_main',
      type: "POST",
      data:  $('#regFormMain').serialize(),
      dataType: "JSON",
      success:function(response){
        $.ajax({
          url : BASE_URL+'pasu/pamain/insertlogs',
          type: "POST",
          data: {prev:prev,current:current,gencode:gencode,id_main:id_main},
          dataType: "JSON",
          success:function(response){
            swal('Update successfully!', '', 'success');
          }
        });
      }
    });
    } else {
      swal("Cancelled", "Your file is safe :)", "error");
         // e.preventDefault();
         var codegen = $('#codegen').val();
          $.ajax({
              type: 'POST',
              data : {codegens:codegen},
              dataType: "JSON",
              url: BASE_URL + 'pasu/pamain/fetchallpainformations',
              success:function(response){
                document.getElementById('pa_name').value = response.output;
              }
          });
    }
  });
});

