$(function(){
    var addedMonth = [];
    $("#addpenalty").click(function(){
    var month   = document.getElementById("monitor_months");
    var strMonth  = month.options[month.selectedIndex].text;
    var idMonth   = $("#monitor_months").val();
    var year   = document.getElementById("monitor_years");
    var strYear  = year.options[year.selectedIndex].text;
    var idYear   = $("#monitor_years").val();
    var code    =   document.getElementById("codegen").value;

    var words = idMonth + idYear;
    var index = $.inArray(words, addedMonth);

    var amount = $("#finespenalty").val();
    var remarks = $("#penalty_remarks").val();

    $.ajax({
        type: 'POST',
        data : {code:code,idMonth:idMonth,idYear:idYear},
        url: BASE_URL + 'pasu/pamain/fetchpenaltydate',
        dataType: "JSON",
        success:function(response){
            if (response.status == "no") {
                swal('Select period covered first');
            }else if(response.status == "Not Empty"){
                swal('You already added this date');
            }else if(response.status == "Empty"){

                if(amount == ""){
                    swal("Input the amount",'','warning');
                      return
                } else {
                    if (index >= 0) {
                        swal("You already added this date");
                    }else{
                    addedMonth.push(words);
                    $("#tablePenalty_sample tbody:last-child").append(
                        "<tr style='text-align: center;background-color:#ffff66;'>"+
                            "<td class='hide'>"+code+"</td>"+
                            "<td>"+strMonth+"</td>"+
                            "<td>"+strYear+"</td>"+
                            "<td>"+amount+"</td>"+
                            "<td>"+remarks+"</td>"+
                            "<td><button type='button' name='remove' data-row='row' class='btn btn-danger btn-xs remove' onclick='deleteRowThreat(this)'>-</button></td>"+
                        "</tr>"
                    );
                    document.getElementById("finespenalty").value='';
                    document.getElementById("penalty_remarks").value='';
                    }
                }
            }
        }
    });
    });
});
