function activitysave1()
{

var files = $('#attraction_img_file_1')[0].files;
var error = '';
var form_data = new FormData();
  var other_data = $('#regFormMain').serializeArray();
  $.each(other_data,function(key,input){
      form_data.append(input.name,input.value);
  });
for(var count = 0; count<files.length; count++)
{
 var name = files[count].name;
 var extension = name.split('.').pop().toLowerCase();
 if(jQuery.inArray(extension, ['gif','png','jpg','jpeg']) == -1)
 {
  error += "Invalid " + count + " Image File"
 }
 else
 {
  form_data.append("attraction_img_file_1[]", files[count]);
 }
}
if(error == '')
{
 $.ajax({
  url:BASE_URL+'pasu/pamain/insert_activity_1',
  method:"POST",
  data:form_data,
  contentType:false,
  cache:false,
  processData:false,
  success:function(data)
  {
   // $('#upload_img_activity2').html(data);
   $('#attraction_img_file_1').val('');
  }
 })
}
else
{
 alert(error);
}
}

