$(document).on('change', '#iwddlongoutput', function(e) {
  var dd_value =  document.getElementById("iwddlongoutput").value;
  var degree_value = parseInt(dd_value, 10);
  document.getElementById("iw_longitude_dms_degrees").value = degree_value;

  var down = document.getElementById('iw_longitude_dms_minutes');
  var min_deg = (dd_value + "").split(".")[1];
  // down.value = ("."+adddec)*60;

  var min_value =0;
  min_value = ("."+min_deg)*60;
  var degree_value2 = parseInt(min_value, 10);
  // down.value = degree_value2;
  if (!isNaN(degree_value2)) {
    down.value = degree_value2;
  }else{
    down.value = 0.00;
  }

  var min_sec = 0;
  min_sec = (min_value + "").split(".")[1];
  var sec_value = ("."+min_sec)*60;
  var seconds = document.getElementById('iw_longitude_dms_seconds');
  if (!isNaN(sec_value)) {
    seconds.value = sec_value;
  }else{
    seconds.value = 0.00;
  }
})
