$(document).on('click','.removeEditHydroParameters',function(){
        var id = $(this).data('id');
        var idp = $(this).closest('div').attr('id');
       swal({
        title: "Are you sure you want to change the data?",
        text: "You will not be able to recover this data!",
        type: "warning",
        showCancelButton: true,
        confirmButtonClass: "btn-danger",
        confirmButtonText: "Yes, delete it!",
        cancelButtonText: "No, cancel!",
        closeOnConfirm: false,
        closeOnCancel: false
        },
        function(isConfirm) {
            if (isConfirm) {
                $.ajax({
                    url: BASE_URL+'/pasu/pamain/deleteHydropParam/'+id,
                    type: 'DELETE',
                    data: 'id='+id,
                    dataType: 'json',
                    error: function() {
                        alert('Something is wrong');
                    },
                    success: function(data) {
                        $("#"+idp).remove();
                        swal("Deleted!", "Your data has been deleted.", "success");
                       // $('#modal-edit-income').modal('hide');
                       fetchHydroParametersrefresh();
                       getTabImageHydrologyDetails();
                    }
                    });
            } else {
                swal("Cancelled", "Your data is safe :)", "error");
        }
    });
});

