function readURLmultipleimagethreat(input) {
    if (input.files && input.files[0]) {
      var reader = new FileReader();
      var formdata = new FormData();

      formdata.append('threat_multi_img', document.getElementById('threat_multi_img').files[0]);
      }
       var other_data = $('#regFormMain').serializeArray();
        $.each(other_data,function(key,input){
            formdata.append(input.name,input.value);
        });

        var file_details    =   document.getElementById("threat_multi_img").files[0];
        var extension       =   file_details['name'].split(/\.(?=[^\.]+$)/);
        var allowed_extension   =   ["jpg","jpeg","png"];
        var check_for_valid_ext =   allowed_extension.indexOf(extension[1]);
        if(file_details['size'] > 1677721600)
        {
          swal('Please upload a file less than 200 MB','','warning');
          document.getElementById('threat_multi_img').value="";
          return false;
        }
        else if(check_for_valid_ext == -1)
        {
          swal('upload valid image file(*.jpg, *.jpeg, *.png)','','warning');
          document.getElementById('threat_multi_img').value="";
          return false;
        }
        else
        {
          $.ajax({
            url : BASE_URL + 'pasu/pamain/threatsmultipleimageupload',
            method: 'POST',
            contentType: false,
            cache: false,
            processData: false,
            data: formdata,
            dataType: "JSON",
            success:function(response){
                $("#threat_multi_img_span").html(response.status);
                $("#uploadthreat_multi_img").val('');
                $("#threat_multi_img").val('');
                getthreatmultipleimageupload();
                }
           });
        }
  }

