function readURLSeagrassCompo(input) {
    if (input.files && input.files[0]) {
      var reader = new FileReader();
      var formdata = new FormData();
      formdata.append('img_seagrassSpeciesCompo', document.getElementById('img_seagrassSpeciesCompo').files[0]);
      reader.onload = function (e) {
      $('#imageseagrassSpeciesCompo').attr('src', e.target.result);
      }
        reader.readAsDataURL(input.files[0]);
      }

      $.ajax({
            url : BASE_URL + 'pasu/pamain/seagrassCompoPhoto',
            method: 'POST',
            contentType: false,
            cache: false,
            processData: false,
            data: formdata,
            dataType: "JSON",
            success:function(response){
                // response.status;
                $("#img_seagrassSpeciesCompospan").html(response.status);
                }
           });
  }

