function calc_rate(){
    var arr = document.getElementsByClassName('qty');
    var total = 0;
    for(var i=0; i<arr.length; i++){
        if (parseFloat(arr[i].value))
            total += parseFloat(arr[i].value);
    }
    var results = document.getElementById('total_rate');
    results.value = total.toFixed(3);
}

function calc_nopomem(){
    var arr = document.getElementsByClassName('nopomem');
    var total = 0;
    for(var i=0; i<arr.length; i++){
        if (parseInt(arr[i].value))
            total += parseInt(arr[i].value);
    }
    var results = document.getElementById('total_no_po_lh');
    results.value = total;
}

