$("#waterclassid").change(function(){
  var output = $('#waterclassid');
  var displays = $('#waterclasssubid');
  $.ajax({
    url  : BASE_URL+'/pasu/pamain/getWaterClassSub/',
    type : 'post',
    dataType : 'JSON',
    data : {
      '<?= $this->security->get_csrf_token_name(); ?>' : '<?= $this->security->get_csrf_hash(); ?>',
      ids : $(this).val(),
    },
    success : function(data)
    {
      if (data.status == true) {
        displays.html(data.message);
      } else if (data.status == false) {
        displays.html('');
      } else {
        displays.html('');
      }
    },
    error : function()
    {
      alert('failed me');
    }
  });
  if (document.getElementById("waterclassid").value == 1) {
    document.getElementById("riverdivs").style.display = "block";
    document.getElementById("parameterdivs").style.display = "none";
    document.getElementById("parameterdivs2").style.display = "none";
    document.getElementById("hydroyearassessdiv").style.display = "block";
    document.getElementById("parameterwater").value = "";
    $("#waterqualitystatus")[0].selectedIndex = 0;
  }else{
    document.getElementById("riverdivs").style.display = "none";
    document.getElementById("parameterdivs").style.display = "block";
    document.getElementById("parameterdivs2").style.display = "block";
    document.getElementById("hydroyearassessdiv").style.display = "block";
    document.getElementById("riverinflow").value = "";
    document.getElementById("riveroutflow").value = "";
    document.getElementById("hydro").value = "";
  }
});

