  $('#project_report_files').change(function(){
  var files = $('#project_report_files')[0].files;
  var error = '';
  var form_data = new FormData();

    var other_data = $('#regFormMain').serializeArray();
    $.each(other_data,function(key,input){
        form_data.append(input.name,input.value);
    });

  for(var count = 0; count<files.length; count++)
  {
   var name = files[count].name;
   var extension = name.split('.').pop().toLowerCase();
   if(jQuery.inArray(extension, ['gif','png','jpg','jpeg']) == -1)
   {
    error += "Invalid " + count + " Image File"
   }
   else
   {
    form_data.append("project_report_files[]", files[count]);
   }
  }
  if(error == '')
  {
   $.ajax({
    url:BASE_URL+'pasu/pamain/upload_project_report', //base_url() return http://localhost/tutorial/codeigniter/
    method:"POST",
    data:form_data,
    contentType:false,
    cache:false,
    processData:false,
    beforeSend:function()
    {
     $('#uploaded_images_project_report').html("<label class='text-success'>Uploading...</label>");
    },
    success:function(data)
    {
     // $('#messagemain').html(data.status);
     $('#uploaded_images_project_report').html(data);
     $('#project_report_files').val('');
    }
   })
  }
  else
  {
   alert(error);
  }
 });


