function readURLPAMOManualOperation(input) {
    if (input.files && input.files[0]) {
      var reader = new FileReader();
      var formdata = new FormData();
      formdata.append('manualoperationpamo', document.getElementById('manualoperationpamo').files[0]);
      }
      var file_details    =   document.getElementById("manualoperationpamo").files[0];
      var extension       =   file_details['name'].split(/\.(?=[^\.]+$)/);
      var allowed_extension   =   ["pdf"];
      var check_for_valid_ext =   allowed_extension.indexOf(extension[1]);
      if(file_details['size'] > 1677721600)
      {
        swal('Please upload a file less than 200 MB','','warning');
        document.getElementById('manualoperationpamo').value="";
        return false;
      }
      else if(check_for_valid_ext == -1)
      {
        swal('upload valid image file(*.pdf)','','warning');
        document.getElementById('manualoperationpamo').value="";
        return false;
      }
      else
      {
          $.ajax({
            url : BASE_URL + 'pasu/pamain/ManualOperationFileUpload',
            method: 'POST',
            contentType: false,
            cache: false,
            processData: false,
            data: formdata,
            dataType: "JSON",
            success:function(response){
                $("#manualoperationpamo_span").html(response.status);
                getPAMOManualOperation();
                $('#manualoperationpamo').val('');
                }
           });
        }

  }
