$('#souvenir_img').change(function(){
  var files = $('#souvenir_img')[0].files;
  var error = '';
  var form_data = new FormData();

    var other_data = $('#regFormMain').serializeArray();
    $.each(other_data,function(key,input){
        form_data.append(input.name,input.value);
    });

  for(var count = 0; count<files.length; count++)
  {
   var name = files[count].name;
   var extension = name.split('.').pop().toLowerCase();
   if(jQuery.inArray(extension, ['gif','png','jpg','jpeg']) == -1)
   {
    error += "Invalid " + count + " Image File"
   }
   else
   {
    form_data.append("souvenir_img[]", files[count]);
   }
  }
  var file_details    =   document.getElementById("souvenir_img").files[0];
  var extension       =   file_details['name'].split(/\.(?=[^\.]+$)/);
  var allowed_extension   =   ["png", "jpg", "jpeg"];
  var check_for_valid_ext =   allowed_extension.indexOf(extension[1]);
  if(file_details['size'] > 200097152)
  {
    swal('Please upload a file less than 200 MB','','warning');
    document.getElementById('souvenir_img').value="";
    return false;
  }
  else if(check_for_valid_ext == -1)
  {
    swal('upload valid image file(*.png, *.jpg, *.jpeg)','','warning');
    document.getElementById('souvenir_img').value="";
    return false;
  }
  else
  {
   $.ajax({
    url:BASE_URL+'pasu/pamain/cepasouveniritem', //base_url() return http://localhost/tutorial/codeigniter/
    method:"POST",
    data:form_data,
    contentType:false,
    cache:false,
    processData:false,
    beforeSend:function()
    {
      document.getElementById('upload_souvenir_img').style.display = "block";
     $('#upload_souvenir_img').html("<div id='loaders_iw' class='loader'></div>");
    },
    success:function(response)
    {
     // $('#messagemain').html(data.status);
     // $('#img_iwmapspan').html(data);
      $('#upload_souvenir_img').html('<div class="col-md-12"><img style="width:500px;height:400px" src="'+BASE_URL+'bmb_assets2/upload/cepa_souvenir/'+response+'" class="img-responsive img-thumbnail img-gallery" /></div>');
      document.getElementById("souvenir_img_span").value = response;

     // fetchiwimage();
    }
   })
  }
 });


