function calculatefinancialrate() {
  var a, b;
  a = parseFloat(document.getElementById("financialtarget").value.replace(/,/g, ""));
    if (isNaN(a) == true) {
    a = 0;
  }
  var b = parseFloat(document.getElementById("financialaccomplishment").value.replace(/,/g, ""));
    if (isNaN(b) == true) {
      b = 0;
  }
  if (b > a) {
    swal('Accomplishement not greater than value to Financial Target','','warning');
    document.getElementById("financialvariance").value = "";
    document.getElementById("financialaccomplishment").value = "";
    document.getElementById("financialvariancepercent").value = "";
    return 0;
  }else{
    document.getElementById("financialvariance").value = numberWithCommas(parseFloat(a - b).toFixed());
    document.getElementById("financialvariancepercent").value = numberWithCommas(parseFloat(((a-b)/a)*100).toFixed())+"%";
    return;
  }
}

